// Prepare search parameters
let searchData;

// From orchestrator
if ($json.city && $json.runId) {
  searchData = {
    city: $json.city,
    region: $json.region || '',
    city_id_2gis: $json.cityId2gis || $json.city_id_2gis || '',
    query: $json.query || '–∫–æ–º–ø—å—é—Ç–µ—Ä–Ω—ã–π –∫–ª—É–± –∫–∏–±–µ—Ä—Å–ø–æ—Ä—Ç',
    runId: $json.runId,
    wave: $json.wave || 1,
    lat: $json.lat,
    lon: $json.lon
  };
} 
// Manual trigger with custom city
else if ($json.city) {
  searchData = {
    city: $json.city,
    region: $json.region || '',
    city_id_2gis: $json.city_id_2gis || '',
    query: $json.query || '–∫–æ–º–ø—å—é—Ç–µ—Ä–Ω—ã–π –∫–ª—É–± –∫–∏–±–µ—Ä—Å–ø–æ—Ä—Ç',
    runId: `manual_${Date.now()}`,
    wave: 0
  };
} 
// Default schedule run
else {
  // Get cities from Redis queue or use default
  searchData = {
    city: '–ú–æ—Å–∫–≤–∞',
    region: '–ú–æ—Å–∫–æ–≤—Å–∫–∞—è –æ–±–ª–∞—Å—Ç—å',
    query: '–∫–æ–º–ø—å—é—Ç–µ—Ä–Ω—ã–π –∫–ª—É–± –∫–∏–±–µ—Ä—Å–ø–æ—Ä—Ç',
    runId: `schedule_${Date.now()}`,
    wave: 0
  };
}

const startTime = Date.now();
console.log(`Starting search for ${searchData.city} (runId: ${searchData.runId})`);

return [{ 
  json: {
    ...searchData,
    startTime
  } 
}];

const searchData = $node['Prepare Search Data'].json;
const yandexResponse = $node['Fetch Yandex Maps'].json;
const twoGisResponse = $node['Fetch 2GIS'].json;

const clubs = [];
const errors = [];

// Parse Yandex results
if (yandexResponse?.statusCode === 200 && yandexResponse?.body?.features) {
  for (const feature of yandexResponse.body.features) {
    const props = feature.properties || {};
    const meta = props.CompanyMetaData || {};
    const phones = (meta.Phones || []).map(p => p.formatted).filter(Boolean);

    if (props.name && props.address) {
      clubs.push({
        name: props.name,
        address: props.address.formatted || '',
        phone: phones[0] || '',
        additionalPhones: phones.slice(1),
        city: searchData.city,
        region: searchData.region,
        source: 'YandexMaps',
        coordinates: {
          lat: feature.geometry?.coordinates?.[1] || 0,
          lon: feature.geometry?.coordinates?.[0] || 0
        },
        url: meta.url || '',
        workingHours: meta.Hours?.text || '',
        categories: (meta.Categories || []).map(c => c.name).join(', '),
        foundAt: new Date().toISOString(),
        runId: searchData.runId
      });
    }
  }
} else {
  errors.push({
    api: 'YandexMaps',
    status: yandexResponse?.statusCode || 'No response',
    error: yandexResponse?.body?.message || 'Failed to fetch data'
  });
}

// Parse 2GIS results  
if (twoGisResponse?.statusCode === 200 && twoGisResponse?.body?.result?.items) {
  for (const item of twoGisResponse.body.result.items) {
    const contacts = {};

    // Extract contacts
    for (const group of (item.contact_groups || [])) {
      for (const contact of (group.contacts || [])) {
        if (!contacts[contact.type]) {
          contacts[contact.type] = [];
        }
        contacts[contact.type].push(contact.value);
      }
    }

    if (item.name && item.address_name) {
      clubs.push({
        name: item.name,
        address: item.address_name,
        phone: (contacts.phone || [])[0] || '',
        additionalPhones: (contacts.phone || []).slice(1),
        email: (contacts.email || [])[0] || '',
        city: searchData.city,
        region: searchData.region,
        source: '2GIS',
        isChain: (item.org?.branch_count || 0) > 1,
        brand: item.org?.name || '',
        branchCount: item.org?.branch_count || 1,
        coordinates: {
          lat: item.point?.lat || 0,
          lon: item.point?.lon || 0
        },
        url: (contacts.website || [])[0] || '',
        telegram: (contacts.telegram || [])[0] || '',
        rubrics: (item.rubrics || []).map(r => r.name).join(', '),
        foundAt: new Date().toISOString(),
        runId: searchData.runId
      });
    }
  }
} else if (twoGisResponse?.statusCode === 429) {
  errors.push({
    api: '2GIS',
    status: 429,
    error: 'Rate limit exceeded',
    retryAfter: twoGisResponse.headers?.['retry-after'] || 60
  });
} else {
  errors.push({
    api: '2GIS',
    status: twoGisResponse?.statusCode || 'No response',
    error: twoGisResponse?.body?.error?.message || 'Failed to fetch data'
  });
}

// Deduplicate
const uniqueClubs = [];
const seen = new Set();

for (const club of clubs) {
  const key = `${club.name.toLowerCase().trim()}_${club.address.toLowerCase().trim()}`;
  if (!seen.has(key)) {
    seen.add(key);
    uniqueClubs.push(club);
  }
}

const elapsedMs = Date.now() - searchData.startTime;
console.log(`Found ${uniqueClubs.length} unique clubs in ${searchData.city} (${elapsedMs}ms)`);

return [{
  json: {
    city: searchData.city,
    runId: searchData.runId,
    clubs: uniqueClubs,
    clubsFound: uniqueClubs.length,
    errors,
    elapsedMs,
    timestamp: new Date().toISOString()
  }
}];

const result = items[0].json;
const clubs = result.clubs;
const operations = [];

// Save each club to Redis Set for deduplication
for (const club of clubs) {
  const clubKey = `${club.city.replace(/\s+/g, '_')}:${club.name.replace(/[^a-zA-Z0-9–ê-–Ø–∞-—è]/g, '_')}`;

  operations.push({
    operation: 'sadd',
    key: 'raw_clubs',
    value: clubKey
  });

  operations.push({
    operation: 'set',
    key: `club:${clubKey}`,
    value: JSON.stringify(club),
    ttl: 30 * 24 * 60 * 60
  });
}

// Save result summary
operations.push({
  operation: 'set',
  key: `result:${result.runId}:${result.city}`,
  value: JSON.stringify({
    city: result.city,
    clubsFound: result.clubsFound,
    errors: result.errors,
    timestamp: result.timestamp
  }),
  ttl: 7 * 24 * 60 * 60
});

return operations.map(op => ({ json: op }));

// Get clubs from the parse results
const result = $node['Parse Results'].json;
const enrichmentTasks = [];

// Create enrichment tasks for high-priority clubs
for (const club of result.clubs) {
  // Prioritize chains and clubs with websites
  if (club.isChain || club.url || club.branchCount > 1) {
    enrichmentTasks.push({
      workflow: 'WF-02',
      club: club,
      runId: result.runId
    });
  }
}

console.log(`Triggering enrichment for ${enrichmentTasks.length} high-priority clubs`);

return enrichmentTasks.length > 0 ? enrichmentTasks : [{ json: { skipped: true } }];

const city = $json.city;
const errors = $json.errors;

console.log(`No clubs found in ${city}. Errors:`, errors);

return [{
  json: {
    city: city,
    clubsFound: 0,
    message: 'No gaming clubs found',
    errors: errors,
    runId: $json.runId,
    timestamp: new Date().toISOString()
  }
}];

// Return final results for the executeWorkflow trigger
const parseResults = $node['Parse Results'].json;

const output = {
  success: true,
  city: parseResults.city,
  clubsFound: parseResults.clubsFound,
  runId: parseResults.runId,
  timestamp: parseResults.timestamp,
  elapsedMs: parseResults.elapsedMs
};

console.log(`WF-01 completed for ${output.city}: ${output.clubsFound} clubs found`);

// Return data for parent workflow
return [{ json: output }];

const searchData = $node['Prepare Search Data']?.json || {};

const error = {
  workflow: 'WF-01 Fetch Gaming Clubs',
  city: searchData.city || 'Unknown',
  runId: searchData.runId || 'Unknown',
  timestamp: new Date().toISOString(),
  error: $json.error?.message || 'Unknown error',
  node: $json.error?.node?.name || 'Unknown',
  stack: $json.error?.stack
};

console.error('WF-01 Error:', error);

return [{ json: error }];
