// Extract club data from input
const club = $json.club || $json;

if (!club.name || !club.city) {
  throw new Error('Invalid club data: missing name or city');
}

console.log(`Starting enrichment for ${club.name} in ${club.city}`);

return [{ json: { club, startTime: Date.now() } }];

// Get original club data
const club = $node['Extract Club Data'].json.club;

// Initialize enriched data
const enrichedClub = {
  ...club,
  enrichedAt: new Date().toISOString(),
  enrichmentSource: 'WF-02'
};

// Check if we have scraping results
const extractedNode = $node['Extract Contacts from HTML'];
if (extractedNode && extractedNode.json) {
  const extracted = extractedNode.json;

  // Process emails
  if (extracted.emails && extracted.emails.length > 0) {
    const emails = extracted.emails.map(e => e.replace('mailto:', '').trim());
    if (!enrichedClub.email) {
      enrichedClub.email = emails[0];
    }
    enrichedClub.additionalEmails = emails.slice(1);
  }

  // Process phones
  if (extracted.phones && extracted.phones.length > 0) {
    const phones = extracted.phones.map(p => p.replace('tel:', '').replace(/[^0-9+]/g, ''));
    if (!enrichedClub.phone) {
      enrichedClub.phone = phones[0];
    }
    enrichedClub.scrapedPhones = phones;
  }

  // Process social media
  if (extracted.telegram && extracted.telegram.length > 0) {
    enrichedClub.telegram = extracted.telegram[0];
  }
  if (extracted.vk && extracted.vk.length > 0) {
    enrichedClub.vk = extracted.vk[0];
  }
  if (extracted.instagram && extracted.instagram.length > 0) {
    enrichedClub.instagram = extracted.instagram[0];
  }

  enrichedClub.scrapeSuccess = true;
} else {
  enrichedClub.scrapeSuccess = false;
}

// Add relevance scoring based on categories
if (club.categories && club.categories.includes('компьютер')) {
  enrichedClub.relevanceScore = 10;
} else if (club.categories && club.categories.includes('игр')) {
  enrichedClub.relevanceScore = 8;
} else {
  enrichedClub.relevanceScore = 5;
}

// Add chain detection and priority logic
if (club.isChain || (club.branchCount && club.branchCount > 1)) {
  enrichedClub.priority = 'high';
  enrichedClub.estimatedPCs = (club.branchCount || 1) * 20;
} else {
  enrichedClub.priority = 'medium';
  enrichedClub.estimatedPCs = 15;
}

// Calculate lead score
enrichedClub.leadScore = enrichedClub.relevanceScore * (enrichedClub.isChain ? 2 : 1);

return [{ json: { enrichedClub } }];

const enrichedClub = $json.enrichedClub;
const runId = $json.runId || enrichedClub.runId || `enrich_${Date.now()}`;

// Prepare data for CRM
const crmData = {
  enrichedClub: enrichedClub,
  runId: runId,
  leadData: {
    title: enrichedClub.name,
    phone: enrichedClub.phone || '',
    email: enrichedClub.email || '',
    address: enrichedClub.address,
    city: enrichedClub.city,
    region: enrichedClub.region,
    source: enrichedClub.source,
    telegram: enrichedClub.telegram || '',
    vk: enrichedClub.vk || '',
    instagram: enrichedClub.instagram || '',
    website: enrichedClub.url || '',
    isChain: enrichedClub.isChain || false,
    brand: enrichedClub.brand || '',
    branchCount: enrichedClub.branchCount || 1,
    coordinates: enrichedClub.coordinates,
    workingHours: enrichedClub.workingHours || '',
    priority: enrichedClub.priority,
    relevanceScore: enrichedClub.relevanceScore,
    estimatedPCs: enrichedClub.estimatedPCs,
    leadScore: enrichedClub.leadScore
  }
};

console.log(`Prepared CRM data for ${crmData.leadData.title}`);

return [{ json: crmData }];

const enrichedClub = $node['Prepare CRM Data'].json.enrichedClub;
const crmResult = $node['Execute WF-03'].json;

const output = {
  success: true,
  clubName: enrichedClub.name,
  city: enrichedClub.city,
  enriched: {
    email: !!enrichedClub.email,
    telegram: !!enrichedClub.telegram,
    vk: !!enrichedClub.vk,
    instagram: !!enrichedClub.instagram,
    scrapeSuccess: enrichedClub.scrapeSuccess
  },
  crmId: crmResult.leadId || null,
  timestamp: new Date().toISOString()
};

console.log(`WF-02 completed for ${output.clubName}`);

return [{ json: output }];

const error = $json.error;
const club = $node['Extract Club Data']?.json?.club || {};

const errorLog = {
  workflow: 'WF-02 Enrich Contact Info',
  clubName: club.name || 'Unknown',
  city: club.city || 'Unknown',
  error: error?.message || 'Unknown error',
  node: error?.node?.name || 'Unknown',
  timestamp: new Date().toISOString()
};

console.error('WF-02 Error:', errorLog);

return [{ json: errorLog }];
