const lead = items[0].json;

// Normalize phone number
let phone = lead.PHONE?.[0]?.VALUE || '';
phone = phone.replace(/[^0-9]/g, '');
if (phone.startsWith('8')) {
  phone = '7' + phone.substring(1);
}

// Build context object
const context = {
  leadId: lead.ID,
  clubName: lead.TITLE || '–ö–æ–º–ø—å—é—Ç–µ—Ä–Ω—ã–π –∫–ª—É–±',
  city: lead.ADDRESS_CITY || lead.UF_CRM_CITY || '–ù–µ —É–∫–∞–∑–∞–Ω',
  region: lead.ADDRESS_PROVINCE || lead.UF_CRM_REGION || '',
  phone: phone,
  email: lead.EMAIL?.[0]?.VALUE || '',
  telegram: lead.UF_CRM_TELEGRAM || lead.IM?.[0]?.VALUE || '',
  status: lead.STATUS_ID,
  isChain: lead.UF_CRM_IS_CHAIN === 'Y',
  brand: lead.UF_CRM_BRAND || '',
  branchCount: parseInt(lead.UF_CRM_BRANCH_COUNT) || 1,
  lastComment: lead.COMMENTS || '',
  priority: lead.UF_CRM_PRIORITY || 'medium',
  estimatedPCs: parseInt(lead.UF_CRM_ESTIMATED_PCS) || 15,
  created: lead.DATE_CREATE,
  modified: lead.DATE_MODIFY,
  runId: `nurture_${lead.ID}_${Date.now()}`
};

console.log(`Processing lead ${context.leadId}: ${context.clubName} (${context.city})`);

return [{ json: { context, originalLead: lead } }];

const context = $json.context;
const lastContactRaw = $node['Get Last Contact Time']?.json?.value;

let hoursSinceLastContact = 9999;
let lastContactDate = null;

if (lastContactRaw) {
  try {
    lastContactDate = new Date(lastContactRaw);
    if (!isNaN(lastContactDate.getTime())) {
      hoursSinceLastContact = Math.floor((Date.now() - lastContactDate.getTime()) / 3600000);
    }
  } catch (e) {
    console.error('Failed to parse last contact date:', e);
  }
}

// Business rules for contact frequency
let minHoursBetweenContacts = 24; // Default
if (context.priority === 'high' || context.isChain) {
  minHoursBetweenContacts = 12;
} else if (context.priority === 'low') {
  minHoursBetweenContacts = 48;
}

const shouldContact = hoursSinceLastContact >= minHoursBetweenContacts;

console.log(`Lead ${context.leadId}: ${hoursSinceLastContact}h since last contact, min interval: ${minHoursBetweenContacts}h, should contact: ${shouldContact}`);

return [{ 
  json: { 
    ...items[0].json,
    shouldContact,
    hoursSinceLastContact,
    lastContactDate,
    minHoursBetweenContacts
  } 
}];

// Get context from previous node
const leadContext = $node['Should Contact?'].json.context;

// Process KB items
const kbItems = $input.all().map(item => {
  try {
    const value = item.json.value;
    if (typeof value === 'string') {
      return JSON.parse(value);
    }
    return value;
  } catch (e) {
    return { content: item.json.value || '' };
  }
});

// Filter relevant KB facts based on context
const relevantFacts = [];

// Priority facts for chains
if (leadContext.isChain) {
  const chainFacts = kbItems.filter(item => 
    item.tags?.includes('chain') || 
    item.content?.toLowerCase().includes('—Å–µ—Ç—å') ||
    item.content?.toLowerCase().includes('—Ñ–∏–ª–∏–∞–ª')
  );
  relevantFacts.push(...chainFacts.slice(0, 2));
}

// City-specific facts
const cityFacts = kbItems.filter(item => 
  item.city === leadContext.city || 
  item.region === leadContext.region
);
relevantFacts.push(...cityFacts.slice(0, 1));

// General Dark Project facts
const productFacts = kbItems.filter(item => 
  item.tags?.includes('product') || 
  item.content?.toLowerCase().includes('dark project') ||
  item.content?.toLowerCase().includes('arena')
);
relevantFacts.push(...productFacts.slice(0, 2));

// Limit total facts
const selectedFacts = relevantFacts.slice(0, 5);

return [{ 
  json: { 
    leadContext,
    kbContext: selectedFacts.map(f => f.content || f.text).join('\n\n'),
    kbFactsCount: selectedFacts.length,
    totalKbItems: kbItems.length
  } 
}];

