const response = items[0].json;
const context = $node['Merge Context'].json.context;

// Check for API errors
if (response.statusCode !== 200) {
  const error = response.body?.error || {};
  console.error('OpenAI API Error:', error);

  // Fallback message
  return [{
    json: {
      context,
      message: `–ü—Ä–∏–≤–µ—Ç! –ó–∞–º–µ—Ç–∏–ª, —á—Ç–æ —É –≤–∞—Å ${context.estimatedPCs} –ü–ö. –ï—Å—Ç—å –æ—Ç–ª–∏—á–Ω–æ–µ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ –ø–æ –∞–ø–≥—Ä–µ–π–¥—É –ø–µ—Ä–∏—Ñ–µ—Ä–∏–∏ Dark Project Arena —Å–æ —Å–∫–∏–¥–∫–æ–π –¥–ª—è ${context.isChain ? '—Å–µ—Ç–µ–π' : '–∫–ª—É–±–æ–≤'}. –ú–æ–∂–µ–º –æ–±—Å—É–¥–∏—Ç—å?`,
      messageSource: 'fallback',
      error: error.message || `API returned ${response.statusCode}`
    }
  }];
}

// Extract message from successful response
const messageContent = response.body?.choices?.[0]?.message?.content || '';

// Validate and trim message
let finalMessage = messageContent.trim();
if (finalMessage.length > 500) {
  finalMessage = finalMessage.substring(0, 497) + '...';
}

if (!finalMessage) {
  // Another fallback
  finalMessage = `–î–æ–±—Ä—ã–π –¥–µ–Ω—å! –£ –Ω–∞—Å —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ–µ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ –¥–ª—è ${context.isChain ? '—Å–µ—Ç–∏ –∫–ª—É–±–æ–≤' : '–∫–ª—É–±–∞'} ${context.clubName}. –ù–æ–≤–∞—è –ª–∏–Ω–µ–π–∫–∞ Dark Project Arena - –∏–¥–µ–∞–ª—å–Ω–æ–µ —Ä–µ—à–µ–Ω–∏–µ –¥–ª—è –∫–∏–±–µ—Ä—Å–ø–æ—Ä—Ç–∞. –ò–Ω—Ç–µ—Ä–µ—Å–Ω–æ?`;
}

return [{
  json: {
    context,
    message: finalMessage,
    messageSource: 'openai',
    usage: response.body?.usage,
    model: response.body?.model
  }
}];

const activity = {
  workflow: 'WF-04 Sales Nurture',
  leadId: $json.context.leadId,
  clubName: $json.context.clubName,
  city: $json.context.city,
  action: 'message_sent',
  channel: $json.sentVia || 'crm_task',
  messageLength: $json.message.length,
  messageSource: $json.messageSource,
  timestamp: new Date().toISOString(),
  runId: $json.context.runId
};

console.log('Nurture activity:', activity);

return [{ json: activity }];

const skipReason = {
  leadId: $json.context.leadId,
  reason: 'contact_interval_not_met',
  hoursSinceLastContact: $json.hoursSinceLastContact,
  minRequired: $json.minHoursBetweenContacts,
  nextContactTime: new Date(Date.now() + ($json.minHoursBetweenContacts - $json.hoursSinceLastContact) * 3600000).toISOString()
};

console.log(`Skipping contact for lead ${skipReason.leadId}: only ${skipReason.hoursSinceLastContact}h passed, need ${skipReason.minRequired}h`);

return [{ json: skipReason }];

const error = {
  workflow: 'WF-04 Sales Nurture',
  timestamp: new Date().toISOString(),
  error: $json.error?.message || 'Unknown error',
  node: $json.error?.node?.name || 'Unknown',
  leadId: $node['Prepare Context']?.json?.context?.leadId || 'Unknown',
  stack: $json.error?.stack
};

console.error('Sales Nurture Error:', error);

return [{ json: error }];
